                // 被删除节点是父节点的左子节点
                if (target == target.parent.left) {
                    // 将target的父节点的left设为null
                    target.parent.left = null;
                } else {
                    // 将target的父节点的right设为null
                    target.parent.right = null;
                }
                target.parent = null;
            }
        } else if (target.left == null && target.right != null) {
            // 左子树为空，右子树不为空
            // 被删除节点是根节点
            if (target == root) {
                root = target.right;
            } else {
                // 被删除节点是父节点的左子节点
                if (target == target.parent.left) {
                    // 让target的父节点的left指向target的右子树
                    target.parent.left = target.right;
                } else {
                    // 让target的父节点的right指向target的右子树
                    target.parent.right = target.right;
                }
                // 让target的右子树的parent指向target的parent
                target.right.parent = target.parent;
            }
        } else if (target.left != null && target.right == null) {
            // 左子树不为空，右子树为空
            // 被删除节点是根节点
            if (target == root) {
                root = target.left;
            } else {
                // 被删除节点是父节点的左子节点
                if (target == target.parent.left) {
                    // 让target的父节点的left指向target的左子树
                    target.parent.left = target.left;
                } else {
                    // 让target的父节点的right指向target的左子树
                    target.parent.right = target.left;
                }
                // 让target的左子树的parent指向target的parent
                target.left.parent = target.parent;
            }
        } else {
            // 左、右子树都不为空
            // leftMaxNode用于保存target节点的左子树中值最大的节点
            Node leftMaxNode = target.left;
            // 搜索target节点的左子树中值最大的节点
            while (leftMaxNode.right != null) {
                leftMaxNode = leftMaxNode.right;
            }
            // 从原来的子树中删除leftMaxNode节点
            leftMaxNode.parent.right = null;
            // 让leftMaxNode的parent指向target的parent
            leftMaxNode.parent = target.parent;
            // 被删除节点是父节点的左子节点
            if (target == target.parent.left) {
                // 让target的父节点的left指向leftMaxNode
                target.parent.left = leftMaxNode;
            } else {
                // 让target的父节点的right指向leftMaxNode
                target.parent.right = leftMaxNode;
            }
            leftMaxNode.left = target.left;
            leftMaxNode.right = target.right;
            target.parent = target.left = target.right = null;
        }
    }

    // 根据给定的值搜索节点
    public Node getNode(T ele) {
        // 从根节点开始搜索
        Node p = root;
        while (p != null) {
            int cmp = ele.compareTo(p.data);
            // 如果搜索的值小于当前p节点的值
            if (cmp  0) {
                // 如果搜索的值大于当前p节点的值
                // 向右子树搜索
                p = p.right;
            } else {
                return p;
            }
        }
        return null;
    }

    // 广度优先遍历
    public List breadthFirst() {

        Queue queue = new ArrayDeque();
        List list = new ArrayList();
        if (root != null) {
            // 将根元素入“队列”
            queue.offer(root);
        }
        while (!queue.isEmpty()) {
            // 将该队列的“队尾”的元素添加到List中
            list.add(queue.peek());
            Node p = queue.poll();
            // 如果左子节点不为null，将它加入“队列”
            if (p.left != null) {
                queue.offer(p.left);
            }
            // 如果右子节点不为null，将它加入“队列”
            if (p.right != null) {
                queue.offer(p.right);
            }
        }
        return list;
    }

    public void treeString(StringBuffer stringBuffer,Node node) {

        if (node == null)
            return;

        stringBuffer.append(node.data).append("((").append(node.data).append(")); \n") ;

        if (node.parent != null ){
            stringBuffer.append(node.parent.data).append("-->").append(node.data).append("; \n") ;
        }

        if (node.left != null ){
            treeString(stringBuffer,node.left) ;
        }

        if (node.right != null) {
            treeString(stringBuffer,node.right) ;
        }

    }

